import React, { useState } from "react";
import { ContinentFilterQuery } from "./Queries";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import "./App.css";
import { useLazyQuery } from "@apollo/react-hooks";

export const Home = () => {
    const [busqueda, setBusqueda] = useState("");


     //traer datos con boton
     const [getCountries, {loading, data}] = useLazyQuery(ContinentFilterQuery,{
            variables: "continent"
     });

        if (loading) return "Cargando.......";
        if (data) {
            console.log(data)
        }

    //  const {loading2, data2, error2} = useQuery(CountryQuery);
            
    //     if (loading2) return "Cargando...";
    //     if (error2) return `Error! ${error2.meesage}`;
    //     if (data2) {
    //         console.log(data2);
    //     } else{
    //         return false
    //     }



    //busqueda
     const buscador = (e) => {
        setBusqueda(e.target.value)
        console.log(e.target.value);
    }


    //filtrado
    let resultados = [];
    if(!busqueda)
    {
        resultados = data
    }else {
        resultados = data.continent.filter((dato) => 
        dato.name.toLowerCase().includes(busqueda.toLocaleLowerCase())
        )
        console.log(resultados);
    };


    //filtrado con boton Continente
    // let groupContinent = [];

    // console.log(groupContinent);




    return (
        <div className="div_principal">
            <div className="div_titulo">
                <h1>Country search</h1>
                <h5>Some random text</h5>
            </div>
            <div>
                <form className="input_general">
                    <span id="icon">
                        <FontAwesomeIcon icon={faMagnifyingGlass}></FontAwesomeIcon>
                    </span>
                    <input
                        className="input_text"
                        type="text"
                        name="code"
                        placeholder="Escribe el paÃ­s a buscar"
                        value={busqueda}
                        onChange={buscador}
                    />
                </form>
            </div>
            <div className="grupo">
                <h2>Group by:</h2>
                <button  
                    type="button"
                    className="boton"
                    onClick={() => getCountries()}
                >Continent
                </button>
                <button type="button" className="boton">Language</button>
            </div>
            <div>
                
                <div>
                    {resultados && resultados.continents.map((country, idx) => {
                        return (
                            <>
                            <h4 className="titulo_pais">{country.continent.name}</h4>
                            <div className="card" key={idx}>
                                <div className="container">
                                    <div className="div0">
                                    <h4 className="titulo_pais">{country.emoji} {country.name}</h4>
                                    </div>
                                    <div className="div1">
                                        <h6>Code: {country.code}</h6>
                                        <h6>Moneda: {country.currency}</h6>
                                    </div>
                                    
                                    <div className="div3">
                                        <h6>Continent: {country.continent.name}</h6>
                                        <h6>Continent code: {country.continent.code}</h6>
                                    </div>
                                </div>
                            </div>
                            </>
                        )
                    })}
                </div>
            </div>
        </div>
    );
};
